sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var rating = g_form.getValue('rating');
	if(rating == 'excellent')
	{
		g_form.showFieldMsg('rating','Thankyou for the excellent rating','info');
	}
	else if(rating == 'good')
	{
		g_form.showFieldMsg('rating','We will do our best to provide you better services','info');
	}
	else if(rating == 'average')
	{
		g_form.showFieldMsg('rating','Please let us know your review in the comment section','info');
	}
	else if(rating == 'poor')
	{
		g_form.showFieldMsg('rating','We deeply regret your experience please write your problems in the comment section','error');
	}


   //Type appropriate comment here, and begin script below

}
