sys_script_include.script:
var AttendeeUtils = Class.create();
AttendeeUtils.prototype = {
    initialize: function(eventid) {
        this.eventid = eventid;
    },
	isRegistered:function(){
		var attendee = new GlideRecord('x_1792505_event_ma_attendee');
		attendee.addQuery('event_id',this.eventid);
		attendee.query();
		return attendee.hasNext();
	},
	getAttendee:function(id) {
        var res = [];
        var attendee = new GlideRecord('x_1792505_event_ma_attendee');
        attendee.addQuery('attendee_id',id);
        attendee.query();
        if (attendee.next()) {
            res.push({
                id: attendee.getDisplayValue(),
				firstname : attendee.getValue('first_name'),
                lastname : attendee.getValue('last_name')
            });
		}
        return JSON.stringify(res);

    },
	getEventAttendee:function() {
        var res = [];
        var attendee = new GlideRecord('x_1792505_event_ma_attendee');
        attendee.addQuery('event_id', this.eventid);
        attendee.query();
        if (attendee.next()) {
            res.push({
                id: attendee.getDisplayValue(),
				firstname : attendee.getValue('first_name'),
                lastname : attendee.getValue('last_name')
            });}
            return JSON.stringify(res);

    },

	fetchAllAttendees: function() {
        var res = [];
        var gr = new GlideRecord('x_1792505_event_ma_attendee'); // Use your attendee table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                first_name: gr.getValue('first_name'),
                last_name: gr.getValue('last_name'),
                email: gr.getValue('email'),
                status: gr.getDisplayValue('status')
            });
        }
        return JSON.stringify(res);
    },

    type: 'AttendeeUtils'
};
